package schema

import (
	"entgo.io/ent"
	"entgo.io/ent/schema/edge"
	"entgo.io/ent/schema/field"
	"github.com/google/uuid"
)

// HomeworkCodeTest holds the schema definition for the HomeworkCodeTest entity.
type HomeworkCodeTest struct {
	ent.Schema
}

// Fields of the HomeworkCodeTest.
func (HomeworkCodeTest) Fields() []ent.Field {
	return []ent.Field{
		field.UUID("id", uuid.UUID{}).
			Default(uuid.New).
			Immutable(),
		field.UUID("task_id", uuid.UUID{}),
		field.String("in"),
		field.String("out"),
		field.Bool("is_hide").
			Default(false),
	}
}

// Edges of the HomeworkCodeTest.
func (HomeworkCodeTest) Edges() []ent.Edge {
	return []ent.Edge{
		edge.From("task", HomeworkTask.Type).
			Ref("code_tests_rel").
			Field("task_id").
			Unique().
			Required(),
	}
}

